header = {
    "alg": "HS256",
    "typ": "JWT"
}
payload = {
    "X-Integration-Token":"Sua chave de API",
    "X-Resource-Token":"JWT do usuário autenticado" 
}
HMACSHA256(
    base64UrlEncode( header ) + '.' +
    base64UrlEncode( payload ),
    (secret da chave anunciada no payload)
)

